import React from 'react'
import '../App.css'
import { useState } from 'react'


const Signup = () => {
  const initialValue = {name: "" , email: "" ,phoneNo :"" ,work :"" ,password:"",cpassword:""}
  const [checkName ,setCheckName] = useState(initialValue)

  const handleChange= (e)=>{
    console.log(e.target)
    const {name,value} = e.target;
    setCheckName({...checkName,[name]:value})
    console.log(checkName)

  }

  // useEffect(()=>{
  //   handleChange();
  // },[])

  return (
   <div className='container'>
        <form className='signup'>
          <h1>SignUp</h1>
          <input type="text" placeholder='Username' value ={checkName.name} onChange={handleChange} /><br /><br />
          <input type="text" placeholder= 'Email' value ={checkName.email} onChange={handleChange}/><br /><br />
          <input type="number" placeholder='Phone No.' value ={checkName.phoneNo} onChange={handleChange}/><br /><br />
          <input type="text" placeholder='Work' value ={checkName.work} onChange={handleChange}/><br /><br />
          <input type="text" placeholder='Password' value ={checkName.password} onChange={handleChange}/><br /><br />
          <input type="text" placeholder='Conform Password' value ={checkName.cpassword} onChange={handleChange}/><br /><br />
        </form>
   </div>
  )
}

export default Signup
