import '../App.css'
import { useState, useEffect } from 'react'


const Signup = () => {
  // const initialValue = {name: "" , email: "" ,phoneNo :"" ,work :"" ,password:"",cpassword:""}
  const initialValue = { name: "" }
  const [checkName, setCheckName] = useState(initialValue)

  const handleChange = (e) => {
    // const {name} = e.target.value;
    setCheckName(e.target.value)
    // setCheckName(e.target.value)
    // console.log(e.target.value)

  }

  useEffect(() => {
    handleChange();
  }, [])

  return (
    <>
      <form className='signup'>
        <h1>SignUp</h1>
        <input type="text" name="name" onChange={handleChange} value={checkName} />
        {/* <input type="text" placeholder='Enter your name' value={checkName} onChange={handleChange} /><br /><br /> */}
        {/* <input type="text" placeholder='Enter your email' value ={checkName.email} onChange={handleChange}/><br /><br />
          <input type="number" placeholder='Enter your phone no.' value ={checkName.phoneNo} onChange={handleChange}/><br /><br />
          <input type="text" placeholder='Enter your work' value ={checkName.work} onChange={handleChange}/><br /><br />
          <input type="text" placeholder='Enter your password' value ={checkName.password} onChange={handleChange}/><br /><br />
          <input type="text" placeholder='Re-enter your password' value ={checkName.cpassword} onChange={handleChange}/><br /><br /> */}
      </form>
    </>
  )
}

export default Signup
