import React from 'react'
import '../App.css'
import { useState ,useEffect} from 'react'


const Signup = () => {
  const initialValue = {username: "" , email: "" ,phoneNo :"" ,work :"" ,password:"",cpassword:""}
  const [formValues ,setFormValues] = useState(initialValue);
  const [formErrors,setFormErrors]=useState({});
  const [isSubmit,setIsSubmit] = useState(false);

  const handleChange= (e)=>{
    const {name,value} = e.target
    setFormValues({...formValues,[name]:value})

  }
  const onSubmit =(e)=>{
    e.preventDefault()
    setFormErrors(validate(formValues))
    setIsSubmit(true);
  }

  useEffect(()=>{
    console.log(formErrors);
    if (Object.keys(formErrors).length === 0 && isSubmit) {
      console.log(formValues);
    }
  },[formErrors])


  const validate=(values)=>{
    const errors ={}
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,3}$/i; ;

    if(!values.username){
      errors.username="*Username is required" ;
    }
    if(!values.email){
      errors.email ="*Email is required"
    }else if(!regex.test(values.email)){
      errors.email="*Email is invalid" ;
    }
    if(!values.phoneNo){
      errors.phoneNo ="*phoneNo is required"
    }else if(values.phoneNo.length <9 || values.length >15){
      errors.phoneNo ="*phoneNo is invalid"
    }
    if(!values.work){
      errors.work ="*work is required"
    }
    if(!values.password){
      errors.password ="*password is required"
    }else if(values.password.length <3){
      errors.password ="*password cannot be less than 3 words"
    }
    if(!values.cpassword){
      errors.cpassword ="*Password is required"
    }else if(values.password !== values.cpassword){
      errors.cpassword ="*Passord does not match"
    }
  }


  return (
   <div className='container'>
    <pre>{JSON.stringify(formValues,undefined,2)}</pre>
        <form className='signup' onSubmit={onSubmit}>
          <h1>SignUp</h1>

          <input
              type="text"
              name="username"
              placeholder="Username"
              value={formValues.username}
              onChange={handleChange}
            />
          <p>{formErrors.username}</p>

          <input type="text" name="email" placeholder= 'Email' value ={formValues.email} onChange={handleChange}/><br /><br />
          <p>{formErrors.email}</p>

          <input type="number" name="phoneNo" placeholder='Phone No.' value ={formValues.phoneNo} onChange={handleChange}/><br /><br />
          <p>{formErrors.phoneNo}</p>

          <input type="text" name="work" placeholder='Work' value ={formValues.work} onChange={handleChange}/><br /><br />
          <p>{formErrors.work}</p>

          <input type="password" name="password" placeholder='Password' value ={formValues.password} onChange={handleChange}/><br /><br />
          <p>{formErrors.password}</p>

          <input type="password" name="cpassword" placeholder='Conform Password' value ={formValues.cpassword} onChange={handleChange}/><br /><br />
          <p>{formErrors.cpassword}</p>

          <button>Submit</button>
        </form>
   </div>
  )
}

export default Signup
